const now = new Date();

const year = now.getFullYear();
const month = String(now.getMonth() + 1).padStart(2, '0'); // MÃªs comeÃ§a em 0
const day = String(now.getDate()).padStart(2, '0');
const hours = String(now.getHours()).padStart(2, '0');
const minutes = String(now.getMinutes()).padStart(2, '0');
const seconds = String(now.getSeconds()).padStart(2, '0');
const milliseconds = String(now.getMilliseconds()).padStart(6, '0');

// Calcula o offset do timezone em minutos
const offsetMinutes = now.getTimezoneOffset();
const offsetHours = String(Math.abs(Math.floor(offsetMinutes / 60))).padStart(2, '0');
const offsetMins = String(Math.abs(offsetMinutes % 60)).padStart(2, '0');
const offsetSign = offsetMinutes > 0 ? '-' : '+';
const timezoneOffset = `${offsetSign}${offsetHours}:${offsetMins}`;

const formattedDate = `${year}-${month}-${day}T${hours}:${minutes}:${seconds}.${milliseconds}${timezoneOffset}`;

insomnia.environment.set("now", formattedDate);
console.log(formattedDate)

